'''炒菜的例子，刚搬家的时候'''from time import ctime, sleepimport threadingdef cook(dish, cost_time):    for i in range(2):        print("我正在炒%s~~~~~~~~ %s" % (dish, ctime()))        sleep(cost_time)# 创建线程对象t1 = threading.Thread(target=cook, args=('煮面条', 5))t2 = threading.Thread(target=cook, args=('煮水饺', 2))print(threading.current_thread().getName())print(t1.getName())print(t2.getName())t1.start()t2.start()# t1.join()t2.join()print("做好了！！！%s" % ctime())