'''炒菜的例子，'''from time import ctime, sleepimport threadingdef cook(dish, cost_time):    for i in range(2):        print("我正在炒%s~~~~~~~~ %s" % (dish, ctime()))        sleep(cost_time)menu = ['软炸里脊', '软炸鸡', '什锦套肠', '麻酥油卷儿']ts=[]for m in menu:    t=threading.Thread(target=cook,args=(m,2))    t.start()    ts.append(t)for t in ts:    t.join()print('做好了，吃饭！')